import React, { useState } from "react"
import styles from './form.module.css'
import { validate } from './validation.js'

export default function Form (props) {
    const {login, successLogin} = props
 const [ userData, setUserData] = useState({
  username: '',
  password: ''
 })

 const [errors, setErrors] = useState({
  ...userData
 })

const handleInputChange = (e) => {
 setUserData({
  ...userData,
  [e.target.name] : e.target.value
 })
 setErrors(validate({
  ...userData,
  [e.target.name] : e.target.value
 }))
}

const handleSubmit = (e) => {
e.preventDefault()
 login(userData)
}

 return (
  <div className={styles.formContainer}>
   <form className={styles.loginForm} onSubmit={handleSubmit}>
   <div className={styles.formItem}>
    <label >Username</label>
    <input 

    className={errors.username && styles.warning}
    type="text" 
    name='username' 
    value={userData.username} 
    onChange={handleInputChange} />
<span className='danger'>{errors.username}</span>
</div>
<div className={styles.formItem}>
    <label >Password</label>
    <input 
    className={errors.password && styles.warning}
    type="text" 
    name='password' 
    value={userData.password} 
    onChange={handleInputChange} />
<span className='danger'>{errors.password}</span>
</div>
    <button type='submit' disabled={Object.keys(errors).length ? true : false} >LOGIN</button>
    <span className={styles.danger} >{successLogin}</span>
   </form>
  </div>
 )
}
